local Calculator={PetTypes={["Unique Pet"]={0,1250,3750,7500,12500,18750,26250,35000,45000,56250,68750,82500,97500,113750,131250,150000,170000,191250,213750,237500},["Epic Pet"]={0,1000,3000,6000,10000,15000,21000,28000,36000,45000,55000,66000,78000,91000,105000,120000,136000,153000,171000,190000,210000},["Rare Pet"]={0,750,2250,4500,7500,11250,15750,21000,27000,33750,41250,49500,58500,68250,78750,90000,102000,114750,128250,142500,157500},["Advanced Pet"]={0,500,1500,3000,5000,7500,10500,14000,18000,22500,27500,33000,39000,45500,52500,60000,68000,76500,85500,95000,105000},["Basic Pet"]={0,250,750,1500,2500,3750,5250,7000,9000,11250,13750,16500,19500,22750,26250,30000,34000,38250,42750,47500,52500},["Unique Aura"]={0,3000,7500,13500,21000,30000,40500,52500,66000,81000,97500,115500,135000,156000,178500,202500,228000,255000,283500,313500,345000}},Rocks={{name='Jungle Rock',multiplier=16.25},{name='Muscle King Rock',multiplier=12.5},{name='Legends Rock',multiplier=2.5},{name='Inferno Rock',multiplier=1.125},{name='Mystic Rock',multiplier=0.75},{name='Frozen Rock',multiplier=0.375},{name='Golden Rock',multiplier=0.2},{name='Large Rock',multiplier=0.075},{name='Punching Rock',multiplier=0.05},{name='Tiny Rock',multiplier=0.025}},CalculateRock=function(self,r,rn,pt)local m=nil;for _,rock in ipairs(self.Rocks)do if rock.name==rn then m=rock.multiplier;break end end;if not m then return"Rock not found"end;local x=self.PetTypes[pt];if not x then return"Pet type not found"end;local t=(r+20)*m;local rp="Calculation Results\n"..rn.." | Rebirth "..r.." | "..pt.."\n";if t~=math.floor(t)or t>x[#x]then return rp.."Not Glitchable\nStrength needed to rebirth: 25000"end;for i,xp in ipairs(x)do if t==xp then return rp.."Level 1, 0xp"end end;local n=nil;for i,xp in ipairs(x)do if xp>t then n=xp;break end end;if not n then return rp.."Not Glitchable\nStrength needed to rebirth: 25000"end;local d=n-t;local l=1;for i,xp in ipairs(x)do if d<=xp then l=i-1;break end end;local b=0;if l>0 and l<#x then b=x[l]end;return rp.."Level "..l..", "..math.floor(d-b).."xp"end};return Calculator
